package com.onetothirty;

public class Nine {
     //ternary operator (largest of three)
	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int t1=5;
     int t2=4;
     int t3=9;
     int num=(t1>t2)?(t1>t3):(t2>t3);
    	 System.out.println("Largest number is "+num);
     
     
	}

}
